import subprocess
import sys
import os
import base64
from aipyapp import runtime

def package_with_embedded_icon():
    try:
        # 内置Base64编码的日历图标（确保有效ICO格式）
        ico_base64 = """"""
        
        # 解码并保存为ICO文件
        icon_path = "embedded_calendar.ico"
        with open(icon_path, "wb") as f:
            f.write(base64.b64decode(ico_base64))
        
        # 检查源文件
        source_file = "csv_to_ics_converter.py"
        if not os.path.exists(source_file):
            raise FileNotFoundError(f"源文件 {source_file} 不存在")
        
        # 打包命令（使用内置图标）
        cmd = [
            sys.executable, "-m", "PyInstaller",
            "--onefile", "--windowed",
            "--name", "CSVToICSConverter_Ultimate",
            "--distpath", ".",
            "--clean",
            f"--icon={icon_path}",  # 使用解码后的内置图标
            source_file
        ]
        
        print(f"正在打包带内置图标版本：{' '.join(cmd)}")
        result = subprocess.run(cmd, capture_output=True, text=True)
        
        # 清理临时图标文件
        if os.path.exists(icon_path):
            os.remove(icon_path)
        
        if result.returncode == 0:
            exe_path = os.path.join(os.getcwd(), "CSVToICSConverter_Ultimate.exe")
            if os.path.exists(exe_path):
                print(f"✅ 带图标版本打包成功：{exe_path}")
                runtime.set_state(True, exe_path=exe_path)
            else:
                raise Exception("打包完成但未找到exe文件")
        else:
            raise Exception(f"打包失败：{result.stderr}")
            
    except Exception as e:
        error_msg = f"❌ 打包失败：{str(e)}"
        print(error_msg, file=sys.stderr)
        runtime.set_state(False, error=error_msg)

if __name__ == "__main__":
    package_with_embedded_icon()